mapper.select:
select count(fpeId) from financeproducts_extract
where
 userId = #{userId}

 and DATE_FORMAT(ExtractTimes,'%Y-%m-%d') >=
 #{startTime}


 and DATE_FORMAT(ExtractTimes,'%Y-%m-%d')<=#{endTime}

SELECT a.FProductsName as name, a.ExtractTimes as time, a.ExtractMoney as outMoney, a.ExtractMoney-ifnull(b.poundage_money,0) as getMoney, b.poundage_money as breachMoney, b.breakrate as profit, case when a.ExtractState="1" then "0" when a.ExtractState="2" then "2" when a.ExtractState="3" then "1" end "state" FROM financeproducts_extract a left JOIN poundage b on a.FPUID = b.fpuid WHERE userId = #{userId}
and DATE_FORMAT(ExtractTimes,'%Y-%m-%d') >= #{startTime}
and DATE_FORMAT(ExtractTimes,'%Y-%m-%d')<=#{endTime}
order by ExtractTimes desc limit #{current} ,#{size}
